package calc

import "testing"

func TestDivide(t *testing.T) {
	if Divide(10, 2) != 5 {
		t.Error("10/2 should be 5")
	}
}

func TestDivideByZero(t *testing.T) {
	defer func() { recover() }()
	Divide(10, 0) // Should handle gracefully
	t.Error("Should have panicked or returned error")
}
